INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1242, 'BO.MSCR023D', '', '<h3><b>{PH_OGG_APP}</b></h3>L''azione avanzata <b>{PH_OGG_APP}</b> permette al funzionario di richiedere il perfezionamento degli allegati mancanti di un''istanza presentata e di cui non è ancora iniziato l''iter istruttorio. Il funzionario può allegare il documento di richiesta perfezionamento.', 1, 1, 'BO - Azione avanzata back office', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
