INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1249, 'BO.MSCR021D', '', '<h3><b>{PH_OGG_APP}</b></h3>L''azione avanzata <b>{PH_OGG_APP}</b> permette al funzionario di consulare, modificare o eliminare delle note inserite durante l''iter istruttorio oppure di inserirne di nuove. Per rendere visibile la nota sul portale del cittadino (dettaglio procedimento sezione istruttoria) occorre togliere la spunta sulla riservatezza della nota.', 1, 1, 'BO - Azione avanzata back office', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1250, 'BO.MSCR022D', '', '<h3><b>BO-Informazioni generali procedimento</b></h3>La finestra racchiude le informazioni generali del procedimento. In base alla profilazione dell''attore in linea sarà possibile modificare le informazioni raccolte durante l''iter istruttorio, come i dati di protocollazione dell''istanza o delle integrazioni, il periodo di consultazione al pubblico (ove previsto), il termine di scadenza e i responsabili del procedimento. Per  procediementi conclusi è possibile modificare l''esito.', 1, 1, 'BO - Informazioni Generali Procedimento', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1251, 'FO.MSCR022D', '', '<h3><b>FO-Informazioni generali procedimento</b></h3>La finestra racchiude le informazioni generali del procedimento. E'' possibile consultare le informazioni raccolte durante l''iter istruttorio quali la scadenza del procedimento, i dati di protocollazione dell''istanza o di eventuali integrazioni, il periodo di consultazione al pubblico (ove previsto), l''esito di conclusione e i responsabili del procedimento.', 1, 1, 'FO - Informazioni Generali Procedimento', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1252, 'PUBWEB.MSCR001P', '', '<h3><b>Portale di pubblicazione procedimenti e osservazioni</b></h3>Il portale permette la consultazione libera dei procedimenti relativi a progetti/attività/opere ubicate nel territorio piemontese. ', 1, 1, 'Portale di pubblicazione procedimenti e osservazioni', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1253, 'PUBWEB.MSCR003P', '', '<h3><b>Elenco procedimenti pubblicati</b></h3>La videata presenta l''elenco dei procedimenti <b>In corso di istruttoria</b> oppure <b>Conclusi</b>, pubblicati sul portale del cittadino. ', 1, 1, 'Elenco procedimenti pubblicati', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1254, 'PUBWEB.MSCR004P', '', '<h3><b>Dettaglio procedimento</b></h3>La videata presenta i dati generali del procedimento, la sezione istruttoria con esito e termine del procedimento e atti di provvedimento finale. E'' presente una sezione con i documenti pubblicati classificati e scaricabili. Per i progetti/opere georeferenziate è possibile visualizzale in mappa.', 1, 1, 'Dettaglio procedimento', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1255, 'PUBWEB.MSCR005P', '', '<h3><b>Ricerca avanzata</b></h3>E'' possibile ricercare i procedimenti presenti in archivio e pubblicati, indicando più filtri di ricerca. Alcuni tipi di procedimento potranno prevedere filtri specifici.', 1, 1, 'Ricerca Avanzata', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1256, 'PUBWEB.MSCR006P', '', '<h3><b>Avvisi al pubblico</b></h3>La videata presenta la lista dei procedimenti più recenti,  per cui è stato pubblicato un <b>Avviso al pubblico</b>', 1, 1, 'Avviso al pubblico', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1257, 'PUBWEB.MSCR007P', '', '<h3><b>Dettaglio osservazione</b></h3>La videata permette di inserire o consultare un''osservazione di un procedimento. L''inserimento dell''osservazione è possibile solo dietro autenticazione e consiste nel caricare il modulo di osservazioni e altri documenti a corredo. Una volta inviata l''osservazione non potrà più essere modificata o cancellata.', 1, 1, 'Dettaglio osservazione', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1258, 'PUBWEB.MSCR008P', '', '<h3><b>Mie Osservazioni</b></h3>La videata presenta l''elenco delle osservazioni create dall''attore in linea. Solo le osservazioni <b>In compilazione</b> possono essere modificate o eliminate. Le osservazioni inviate saranno consultate dalla pubblica amministrazione che provvederà a pubblicarle sul portale. Una volta pubblicate compariranno nel dettaglio procedimento nella sezione <b>Documenti</b> classificate come <b>Osservazioni</B>.', 1, 1, 'Dettaglio osservazione', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
