INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1301, 'FO.VIA.VAL.QDR_ALLEGATO.download_lettera', '', '<h3><b>Sezione lettera di trasmissione perfezionamento/integrazione</b></h3>Premendo sul pulsante <b>Scarica Lettera di trasmissione</b> si potrà visionare la lettera precompilata dal SISTEMA, includendo l''eventuale testo inserito precedentemente. La lettera andrà firmata digitalmente e poi ricaricata a sistema attraverso l''apposito pulsante.', 1, 3, 'FO - Sezione Lettera accompagnamento perfezionamento/integrazione', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1302, 'FO.VIA.VAL.QDR_ALLEGATO.testo_lettera', '', '<h3><b>Sezione lettera di trasmissione perfezionamento/integrazione</b></h3>Se si vuole aggiungere del testo aggiuntivo alla lettera di trasmissione che verrà generata in automatico dal SISTEMA, occorre inserirlo nel campo <b>Testo aggiuntivo</b> e premere + AGGIUNGI. Il testo può essere modificato o cancellato prima di premere il pulsant <b>Scarica Lettera di trasmissione</b>', 1, 3, 'FO - Sezione Lettera accompagnamento perfezionamento/integrazione', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1303, 'FO.VIA.VAL.QDR_ALLEGATO.upload_lettera_firmata', '', '<h3><b>Sezione lettera di trasmisisone perfezionamento/integrazione</b></h3>Una volta presa visione della lettera di trasmissione scaricata occorre firmarla digitalmente e caricarla a sistema attraverso il pulsante <b>Carica Lettera Firmata</b>', 1, 3, 'FO - Sezione Lettera accompagnamento perfezionamento/integrazione', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1304, 'FO.VIA.VAL.QDR_ALLEGATO.conferma_integrazione', '', '<h3><b>Sezione lettera di trasmissione perfezionamento/integrazione</b></h3>Premendo il pulsante <b>Conferma</b> si invieranno i documenti interessati all''autorità competente. Se si desidera aggiungere od eliminare dei documenti dall''invio occorre premere il pulsante <b>ANNULLA</b> che eliminerà i file <b>Elenco</b> e <b>Lettera di trasmissione</b>, disbiliterà la sezione della lettera di trasmissione e riabiliterà la sezione documenti e il pulsante <b>Prosegui</b>. In questo modo sarà possibile rivedere i documenti interessati e confermare l''invio.', 1, 3, 'FO - Sezione Lettera accompagnamento perfezionamento/integrazione', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
