INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1721, 'BO.I_CAT.PER_M.QDR_ALLEGATO.nota_motivo', '', '<h3><b>Nota/Motivazione Documento</b></h3> <div align="justify"><br>In caso di informazioni aggiuntive sul singolo documento è possibile inserire una descrizione nel campo “NOTA/motivazione”.<br> Per i <U>documenti soggetti a segreto industriale o commerciale</U>: nello stesso campo è obbligatorio indicare la MOTIVAZIONE per la quale, ai sensi dell’art. 9, comma 4 del d.lgs. 152/2006, si richiede che non siano resi pubblici gli elaborati inseriti nella categoria allegati denominata “Documentazione soggetta al segreto industriale”. </div> ', 1, 3, 'BO - Maschera Gestione Allegati documentali - NOTE MOTIVAZIONE', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
