INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1849, 'FO.SC.AdR.QDR_SOGGETTO.testo_iniziale', '', '<h3><b>Gestione Soggetti</b></h3> <div align="justify"><br>Cerca il soggetto proponente utilizzando il codice fiscale dell''Ente/Società, compila o verifica i dati anagrafici e seleziona INSERISCI SOGGETTO per procedere con la presentazione dell''istanza. Se il soggetto non è presente, compila le informazioni richieste e infine seleziona il pulsante INSERISCI SOGGETTO.<br><br> <i> Tutte le informazioni indicate con (*) sono obbligatorie.</i></div>', 1, 2, 'FO - Quadro Soggetti di Siti Contaminati- Testo iniziale - AdR', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1850, 'FO.SC.SnC.QDR_SOGGETTO.ruolo_ricoperto', '', '<h3><b>Seleziona ruolo compilante</b></h3><div align="justify"><br>Selezionare il ruolo della persona fisica che compila l''istanza a sistema (compilante).<br><br>Il <b>compilante</b> può essere un delegato senza potere di firma oppure può essere il dichiarante stesso.<br><br>Il <b>dichiarante</b> è il soggetto che sottoscrive l’istanza, titolato a rappresentare il soggetto proponente per tale finalità (titolato direttamente in relazione al ruolo svolto nella propria organizzazione aziendale oppure tramite delega da allegare) e può assumere i seguenti ruoli nei confronti del proponente (in qualità di):<br>• Legale rappresentante<br>• Delegato con potere di firma<br><br><i> Tutte le informazioni indicate con (*) sono obbligatorie.</i></div>', 1, 3, 'FO - SC SnC- Quadro soggetti - ruolo compilante', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
