INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1869, 'FO.SC.AdR.QDR_OGGETTO_PdC.aree_protette', '', '<div align="justify">Da compilare solo se il progetto ricade totalmente o parzialmente su una o più Aree Protette come definite da L.394/1991 oppure se, pur non ricadendo neanche parzialmente, può avere interferenze su una o più Aree protette.</div>', 2, 2, 'FO - Quadro Oggetto - Testo tab aree protette RC-IICAT PRAE_AE', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1873, 'FO.SC.AdR.QDR_ALLEGATO.conferma_integrazione', '', '<h3><b>Sezione lettera di trasmissione perfezionamento/integrazione</b></h3>Premendo il pulsante <b>Conferma</b> si invieranno i documenti interessati all''autorità competente. Se si desidera aggiungere od eliminare dei documenti dall''invio occorre premere il pulsante <b>ANNULLA</b> che eliminerà i file <b>Elenco</b> e <b>Lettera di trasmissione</b>, disbiliterà la sezione della lettera di trasmissione e riabiliterà la sezione documenti e il pulsante <b>Prosegui</b>. In questo modo sarà possibile rivedere i documenti interessati e confermare l''invio.', 1, 3, 'FO - Sezione Lettera accompagnamento perfezionamento/integrazione', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1874, 'FO.SC.AdR.QDR_ALLEGATO.download_lettera', '', '<h3><b>Sezione lettera di trasmissione perfezionamento/integrazione</b></h3>Premendo sul pulsante <b>Scarica Lettera di trasmissione</b> si potrà visionare la lettera precompilata dal SISTEMA, includendo l''eventuale testo inserito precedentemente. La lettera andrà firmata digitalmente e poi ricaricata a sistema attraverso l''apposito pulsante.', 1, 3, 'FO - Sezione Lettera accompagnamento perfezionamento/integrazione', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1875, 'FO.SC.AdR.QDR_ALLEGATO.nota_motivo', '', '<h3><b>Nota/Motivazione Documento</b></h3> <div align="justify"><br>In caso di informazioni aggiuntive sul singolo documento è possibile inserire una descrizione nel campo “NOTA/motivazione”.<br> Per i <U>documenti soggetti a segreto industriale o commerciale</U>: nello stesso campo è obbligatorio indicare la MOTIVAZIONE per la quale, ai sensi dell’art. 9, comma 4 del d.lgs. 152/2006, si richiede che non siano resi pubblici gli elaborati inseriti nella categoria allegati denominata “Documentazione soggetta al segreto industriale”. </div> ', 1, 3, 'FO - Maschera Gestione Allegati documentali - NOTE MOTIVAZIONE', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
INSERT INTO scriva_d_help (id_help, chiave_help, valore_campo_help, des_testo_help, id_tipo_help, id_livello_help, note_help, gest_data_ins, gest_attore_ins, gest_data_upd, gest_attore_upd, gest_uid ) VALUES(1876, 'FO.SC.AdR.QDR_ALLEGATO.testo_iniziale', '', '<h3><b>Gestione allegati documentali</b></h3> <div align="justify"> Per allegare un documento seleziona la categoria e quindi il tipo di allegato al quale ricondurre il file che si sta caricando.<br> I formati accettati sono: .cpg, .dbf, .jpeg, .jpg, .p7m, .pdf, .png, .prj, .sbn, .sbx, .shp; peso massimo: 100 MB.<br><br> <i>Per le categorie contrassegnate con (*) occorre obbligatoriamente inserire almeno un documento. </i> </div>', 1, 3, 'FO - Maschera Gestione Allegati documentali - frase iniziale', current_timestamp, 'SISTEMA', current_timestamp, 'SISTEMA', gen_random_uuid ());
