// #### NOTA BENE: Per gestire il flusso bisgona assegnare alla variabile "result" un valore booleano ####
// Recupero, dall'oggetto "data" tutte le informazioni presenti nel form
var formData = data;
// Verifico che esista l'oggetto
if (!formData) {
  result = false;
  return result;
}

// Estraggo tramite chiave definita per il controller, le informazioni per il controllo
var categorieProg = formData.JS_CATEGORIE_PROGETTUALI; // Oggetto
// Verifico che esista l'oggetto
if (!categorieProg) {
  result = false;
  return result;
}

// Estraggo il valore effettivo dal dato per il controller
var elencoCat = categorieProg.elenco_categorie; // Array di oggetti CategoriaOggetto
// Verifico che esista l'oggetto
if (elencoCat && elencoCat.length > 0) {
  result = true;
  return result;
} else {
  result = false;
  return result;
}

components.customConditional:
// Recupero, dall'oggetto "data" tutte le informazioni presenti nel form
var formData = data;
// Verifico che esista l'oggetto
if (!formData) { return false; }

// Estraggo tramite chiave definita per il controller, le informazioni per il controllo
var categorieProg = formData.JS_CATEGORIE_PROGETTUALI; // Oggetto
// Verifico che esista l'oggetto
if (!categorieProg) { return false; }

// Estraggo il valore effettivo dal dato per il controller
var elencoCat = categorieProg.elenco_categorie; // Array di oggetti CategoriaOggetto
// Verifico che esista l'oggetto
if (!elencoCat || elencoCat.length == 0) { return false; }

// Ho una lista di elementi, vado a verificare se esiste l'elemento "Altro"
var codCatOgg = 'ALTRO';
// Cerco all'interno dell'array per cod_categoria_oggetto
var categoriaAltro = elencoCat.find((categoria) => {
  // Effettuo un controllo per codici categoria oggetto
  return categoria.cod_categoria_oggetto == codCatOgg;
  // #
});
// Definisco un flag di comodo
var hasCatAltro = categoriaAltro != undefined;

// La visibilità è dettata dal fatto che la categoria "Altro" sia presente
return hasCatAltro;

