// Recupero tutte le chiavi della sezione del componente
var list = Object.assign({}, input.JS_DECODIFICA_PROGETTO.MEZZI_MECCANICI.mezzi_aerei_imbarcazioni, input.JS_DECODIFICA_PROGETTO.MEZZI_MECCANICI.mezzi_movimento_terra, input.JS_DECODIFICA_PROGETTO.MEZZI_MECCANICI.mezzi_pesanti)

// Controlla se dentro la variabile "component" puoi accedere direttamente a tutti i "components" e poi tramite chiave ti tiri fuori solo i flag.
var flgList = Object.keys(list)
//Filter Object by key "flg_"
.filter((key) => key.includes("flg_"))
.reduce((obj, key) => {
return Object.assign(obj, {
  [key]: list[key]
});
}, {});

//Filter flgList
flgList = Object.fromEntries(Object.entries(flgList).filter(([_, v]) => v != ""));
// Verifico che o il flag "non pertinente" o uno dei flag dei vari mezzi sia settato a true
if(Object.keys(flgList).length > 0 || input.JS_DECODIFICA_PROGETTO.MEZZI_MECCANICI.flg_non_pertinente) {
  valid = true;
} else {
  valid = false;
}

